import React, { useCallback, useState, useEffect } from 'react';
import { SelectBox, TextInput, PrimaryButton } from '../components/UIkit';
import { useDispatch } from 'react-redux';
import { saveProduct } from '../redux/products/operations';
import ImageArea from '../components/Products/imageArea'; 
import { db } from '../firebase/index';

const ProductEdit = () => {
    const dispatch = useDispatch();
    let id = window.location.pathname.split('/product/edit')[1];

    if(id !== ""){
        id = id.split('/')[1];
    }

    const [name, setName] = useState(""),
          [description, setDiscription] = useState(""),
          [category, setCategory] = useState(""),
          [gender, setGender] = useState(""),
          [price, setPrice] = useState(""),
          [images, setImages] = useState([]);

    const InputName = useCallback((event) => {
        setName(event.target.value)
    },[setName]);

    const InputDiscription = useCallback((event) => {
        setDiscription(event.target.value)
    },[setDiscription]);

    const InputPrice = useCallback((event) => {
        setPrice(event.target.value)
    },[setPrice]);

    const categoies = [
        {id:"tops", name:"トップス"},
        {id:"shirt", name:"シャツ"},
        {id:"pants", name:"パンツ"}
    ];

    const genders = [
        {id:"all", name:"すべて"},
        {id:"male", name:"メンズ"},
        {id:"female", name:"レディース"}
    ];

    useEffect(() => {
       if(id !== ""){
           db.collection('products').doc(id).get()
                .then(snapshot => {
                    const data = snapshot.data()
                    console.log(data);
                })
       } 
    },[id]);

    return(
        <section>
            <h2 className="u-text__headline u-text-center">商品の登録と編集</h2>
            <div className="c-section-container">
                <ImageArea images={images} setImages={setImages} />
                <TextInput
                    fullWidth={true} label={"商品名"} multiline={false} required={true}
                    rows={1} value={name} type={"text"} onChange={InputName}
                />
                <TextInput
                    fullWidth={true} label={"商品説明"} multiline={true} required={true}
                    rows={5} value={description} type={"text"} onChange={InputDiscription}
                />
                <SelectBox
                    label={"カテゴリー"} required={true} options={categoies} select={setCategory} value={category}
                />
                <SelectBox
                    label={"性別"} required={true} options={genders} select={setGender} value={gender}
                />
                <TextInput
                    fullWidth={true} label={"価格"} multiline={false} required={true}
                    rows={1} value={price} type={"number"} onChange={InputPrice}
                />
                <div className="module-spacer--medium" />
                <div className="center">
                    <PrimaryButton
                        label={"商品情報を保存"} onClick={() => dispatch(saveProduct(name, description, category, gender, price, images))}
                    />
                </div>
            </div>
        </section>
    )
}

export default ProductEdit;
