import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { getKeyword, getProducts } from '../../redux/products/selector';
import { fetchSearchProducts } from '../../redux/products/operations';
import ProductCard from './ProductCard';

const SearchProduct = () => {
    const dispatch = useDispatch();
    const selector = useSelector((state) => state);
    const keyword = getKeyword(selector);
    const products = getProducts(selector);

    // const query = selector.router.location.search;
    // const gender = /^\?gender=/.test(query) ? query.split('?gender=')[1] : "";
    // const category = /^\?category=/.test(query) ? query.split('?category=')[1] : "";

    useEffect(() => {
        dispatch(fetchSearchProducts())
    },[dispatch]);

    // const searchFilter = () => {
    //     const filter = [];

    //     products.map(product => {
    //         if(keyword.startsWith(product.name)){
    //             filter.push(product)
    //         }
    //         return filter;
    //     })
    // };

    const filter = products.filter((product) => {
        return keyword.indexOf(product.name) === 0;
    });

    console.log(filter);
    // console.log(keyword);
    // console.log(searchFilter());

    return(
        <section className="c-section-wrapin">
            <div className="p-grid__row">
                {/* {products.map(product => {
                    product.name.startsWith(keyword) ? (
                        <ProductCard
                            key={product.id} id={product.id} name={product.name}
                            images={product.images} price={product.price}  
                        />
                    ) : (
                        <div>そのキーワードが含まれている商品はありません。</div>
                    )
                })} */}
            </div>
        </section>
    )
}

export default SearchProduct;
