import React, { useCallback, useState } from 'react';
import { SelectBox, TextInput } from '../components/UIkit';

const ProductEdit = () => {

    const [name, setName] = useState(""),
          [description, setDiscription] = useState(""),
          [category, setCategory] = useState(""),
          [gender, setGender] = useState(""),
          [price, setPrice] = useState("");

    const InputName = useCallback((event) => {
        setName(event.target.value)
    },[setName]);

    const InputDiscription = useCallback((event) => {
        setDiscription(event.target.value)
    },[setDiscription]);

    const InputPrice = useCallback((event) => {
        setPrice(event.target.value)
    },[setPrice]);

    const categoies = [
        {id:"tops", name:"トップス"},
        {id:"shirt", name:"シャツ"},
        {id:"pants", name:"パンツ"}
    ];

    return(
        <section>
            <h2 className="u-text__headline u-text-center">商品の登録と編集</h2>
            <div class="c-section-container">
                <TextInput
                    fullWidth={true} label={"商品名"} multiline={false} required={true}
                    rows={1} value={name} type={"text"} onChange={InputName}
                />
                <TextInput
                    fullWidth={true} label={"商品説明"} multiline={true} required={true}
                    rows={5} value={description} type={"text"} onChange={InputDiscription}
                />
                <SelectBox
                    label={"カテゴリー"} required={true} options={categoies} select={setCategory} value={category}
                />
                <TextInput
                    fullWidth={true} label={"価格"} multiline={false} required={true}
                    rows={1} value={price} type={"number"} onChange={InputPrice}
                />
            </div>
        </section>
    )
}

export default ProductEdit;
